		[Test]
		public async Task AddAsync_ShouldAddMountainToDb()
		{
			var mountainForm = new MountainFormViewModel()
			{
				Name = "Витоша",
				Location = "Витоша се намира между Стара планина и Рило-Родопския масив и е част от Средногорието. Тя е единствената куполообразна планина в България и основното и било се откроява добре от останалите планински масиви. То върви по върховете Черни връх, Резньовете, Скопарник и Острица, в посока северозапад-югоизток.",
				Climate = "Климатът на Витоша над 1800 м.н.в е типично планински, с голяма валежна сума 1250 mm. В най-ниските ѝ части годишното количество на валежите достига 700 mm, а в по-високите тази стойност надминава 1000 mm. На Черни връх валежите са 1050 mm. Твърдите валежи от сняг и град представляват над 50% от общото количество на годишните валежи. Средната януарска температура за планината е -4 \u00b0С, а на Черни връх е -8 \u00b0С. Летните температури също не са особено високи. Средната юлска температура е 13 \u00b0С, а на Черни връх 8 \u00b0С. Абсолютната максимална температура на Черни връх е +24 \u00b0С, а абсолютната минимална е -27,4 \u00b0С. Основни за планината са югозападните ветрове. Често ветровете в по-високите части достигат голяма скорост и вследствие тяхното действие остават обширни ветровали. По билото на Витоша средната годишна скорост на вятъра е над 10 m/s. Общото време на слънчевото греене е сравнително малко, като за средните части на планината то е 2400 h, а за Черни връх то е по-малко от 2000 h.",
				Waters = "Във Витоша има голям брой малки водни източници, но единствената голяма река, която извира от планината, е Струма. Тя тече на юг, като преминава през България и Гърция, и се влива в Бяло море.\r\n\r\nОстаналите реки във Витоша са: Рударщица, Палакария, Вълчи дол, Железнишка (Селска), Бистришка (Стара), Янчовска, Раковец, Шумако, Симеоновска, Скакавица, Боянска и Владайска, с притоците си Дамянова, Бистрата и Пладнешка.[2]\r\n\r\nНа територията на Витоша има и 6 подземни реки, в галериите на пещера Духлата, край село Боснек.",
				Fauna = "Едрите бозайници са малко. Това се дължи предимно на незаконния лов. Вследствие на това от тези животни са останали едва няколко вида – благороден елен, сърна, дива свиня, кафява мечка и вълк като и техния брой не е особено голям. Освен тези видове се срещат още водни земеровки, белокоремна белозъбка, обикновен заек, обикновен сънливец, лисица, видра и други. Установени са 236 вида птици, от които 120 гнездят на територията на Витоша.",
				Flora = "Горите заемат над 50% от цялата територия на планината, пасищата – 25%, а останалите площи са заети от скални масиви, каменни сипеи и площи, които поради релефни особености не могат да бъдат залесени.\r\n\r\nНа територията на Витоша са установени около 1500 вида висши растения, което е близо 50% от всички описани в България. Сред тях има 24 български и балкански ендемита и 52 вида включени в Червената книга на България.",
				ImageUrls = "https://upload.wikimedia.org/wikipedia/commons/thumb/d/d3/Vitosha_seen_from_the_center_of_Sofia.jpg/250px-Vitosha_seen_from_the_center_of_Sofia.jpg"
			};

			await _mountainService.AddAsync(mountainForm);

			var allMountains = await _mountainService.GetAllMountains();

			Assert.That(allMountains.Count(), Is.EqualTo(3));

			var mountain = await _mountainService.DetailsAsync(3);

			Assert.That(mountain.Name, Is.EqualTo(mountainForm.Name));
			Assert.That(mountain.Location, Is.EqualTo(mountainForm.Location));
			Assert.That(mountain.Climate, Is.EqualTo(mountainForm.Climate));
			Assert.That(mountain.Waters, Is.EqualTo(mountainForm.Waters));
			Assert.That(mountain.Fauna, Is.EqualTo(mountainForm.Fauna));
			Assert.That(mountain.Flora, Is.EqualTo(mountainForm.Flora));
			Assert.That(mountain.ImageUrls, Is.EqualTo(mountainForm.ImageUrls));
		}

