		[Test]
		public async Task EditGetAsync_ShouldGetInformationAboutRouteForEdit()
		{
			var route = await _routeService.DetailsAsync(1);

			route.Difficulty = Difficulty.Moderate.ToString();

			var result = await _routeService.EditGetAsync(1);

			Assert.That(result, Is.Not.Null);
			Assert.That(result.Id, Is.EqualTo(route.Id));
			Assert.That(result.Name, Is.EqualTo(route.Name));
			Assert.That(result.Description,
				Is.EqualTo(route.Description));
			Assert.That(result.StartingPoint, Is.EqualTo(route.StartingPoint));
			Assert.That(result.Difficulty.ToString(), Is.EqualTo(route.Difficulty.ToString()));
			Assert.That(result.DisplacementPositive, Is.EqualTo(route.DisplacementPositive));
			Assert.That(result.DisplacementNegative, Is.EqualTo(route.DisplacementNegative));
			Assert.That(result.Duration, Is.EqualTo(route.Duration));
			Assert.That(result.ImageUrl, Is.EqualTo(route.ImageUrl));
		}

