		[Test]
		public async Task DetailsAsync_ShouldReturnRouteDetails()
		{
			var result = await _routeService.DetailsAsync(1);

			result.Difficulty = Difficulty.Moderate.ToString();

			Assert.That(result, Is.Not.Null);
			Assert.That(result.Id, Is.EqualTo(1));
			Assert.That(result.Name, Is.EqualTo("Връх Безбог"));
			Assert.That(result.Description,
				Is.EqualTo(
					"Връхът може да се изкачи по два маршрута. Най-популярният е като тръгнете към “душеватката” и Попово езеро, след което се изкачите на седловината между върховете Полежан и Безбог (вторият етап на прехода, който сме описали тук, но в обратна посока). Маршрутът тръгва покрай десния бряг Безбожкото езеро, където вдясно ще видите изоставена постройка. Това са руините на старата хижа „Безбог“, която е разрушена от лавина през 1971 година. Пътеката към върха минава вляво на постройката. По тази пътека няма маркировка, но „пирамидките“ от камъни могат да ви водят.Поглед към връх Безбог в Пирин.Пътеката е доста стръмна и през по- голямата част е през клекове. Малко преди върха започва каменист участък, който също е стръмен. Внимавайте къде стъпвате между камъните.На самия връх има кръст и веднага ще го познаете щом го видите.Гледката оттук е много красива: на Изток част от Кременското езеро, до него върховете Севрия, Джано, Джангал както и част от Поповото езеро. В другата посока се виждат връх Полежан, Стражите и Десилица."));
			Assert.That(result.StartingPoint, Is.EqualTo("Хижа Безбог"));
			Assert.That(result.Difficulty, Is.EqualTo(Difficulty.Moderate.ToString()));
			Assert.That(result.DisplacementPositive, Is.EqualTo(450));
			Assert.That(result.DisplacementNegative, Is.EqualTo(450));
			Assert.That(result.Duration, Is.EqualTo("0.02:40"));
			Assert.That(result.ImageUrl,
								Is.EqualTo("https://planinka.bg/wp-content/uploads/2022/09/DSCF5440-1024x819.webp"));
			Assert.That(result.MountainId, Is.EqualTo(3));
		}

